(14, 'Notulen Rapat ', '<p><strong>NOTULEN RAPAT</strong></p>\r\n\r\n<p>Nama Rapat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Rapat Panitia Seleksi CASN Di Lingkungan Pemkab. OKU Formasi Tahun 2021</p>\r\n\r\n<p>Hari/Tanggal&nbsp;&nbsp;&nbsp;&nbsp; : Selasa / 08 Juni 2021</p>\r\n\r\n<p>Waktu&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 09.00 WIB s/d Selesai</p>\r\n\r\n<p>Tempat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Ruang Bina Praja Pemkab. OKU</p>\r\n\r\n<p>Agenda Rapat&nbsp;&nbsp; : 1.&nbsp; Pengelompokan Formasi Khusus CPNS</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.&nbsp; Persyaratan Pendaftaran</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.&nbsp; Juknis Verifikator dalam Seleksi Administrasi</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>PENGELOMPOKAN FORMASI KHUSUS CPNS</li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.gif\" style=\"height:17px; width:17px\" /><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.gif\" style=\"height:17px; width:17px\" />Putera/Puteri Lulusan Terbaik (Cumlaude)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; YA, sebanyak : 1 (satu)&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TIDAK</li>\r\n</ol>\r\n\r\n<p>1. Ahli pertama-Polisi Pamong Praja ...............................................................................................................................................................</p>\r\n\r\n<ol>\r\n	<li><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.gif\" style=\"height:17px; width:16px\" /><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.gif\" style=\"height:17px; width:17px\" />Disabilitas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; YA, sebanyak : 2 (dua)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TIDAK</li>\r\n</ol>\r\n\r\n<p>1.Ahli pertama- Administrator Kesehatan</p>\r\n\r\n<p>2.Verifikator Keuangan ................................................................................................................................................................</p>\r\n\r\n<ol>\r\n	<li><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image005.gif\" style=\"height:17px; width:17px\" /><img src=\"file:///C:\\Users\\DITAIN~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.gif\" style=\"height:17px; width:17px\" />Diaspora&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp